import React , { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-buttons/js/dataTables.buttons.js";
import "datatables.net-buttons/js/buttons.colVis.js";
import "datatables.net-buttons/js/buttons.flash.js";
import "datatables.net-buttons/js/buttons.html5.js";
import "datatables.net-buttons/js/buttons.print.js";
//import Jquery
import $ from 'jquery'; 
import axios from 'axios';
import 'jquery/dist/jquery.min.js';
import "datatables.net-dt/css/jquery.dataTables.min.css";

export default class Manageoperator extends Component {
    // State array variable to save and show data
    constructor(props) {
      super(props)
       this.state = {
        data: [],
      }
  }
  componentDidMount() {
     //Get all users details in bootstrap table
      axios.get('https://jsonplaceholder.typicode.com/comments').then(res =>  {
        //Storing users detail in state array object
        this.setState({data: res.data});
      }); 
      //initialize datatable
      $(document).ready(function () {
          setTimeout(function(){
          $('#example').DataTable(
              {
                  retrieve: true,
                  pagingType: 'full_numbers',
                  pageLength: 10,
                  processing: true,
                  dom: 'Bfrtip',
                      buttons: ['copy', 'csv', 'print'
                  ]
              }
          );
          } ,
          1000
          );
      });
  }
  render(){
      return (
          <>
            <div className="row">
                  <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                    <div className="page-header" style={{marginLeft: "268px"}}>
                        <h2 className="pageheader-title">Manage Operators</h2>
                        <div className="page-breadcrumb">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb">
                                    <li className="breadcrumb-item"><a href="<?php echo base_url(); ?>dashboard" className="breadcrumb-link">Forest Departmen</a></li>
                                    <li className="breadcrumb-item active" aria-current="page">Manage Operators</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                  </div>
              </div>
              <div className="MainDiv my-lg-4" style={{marginLeft: "210px" }}>
                  <div className="m-md-5 p-5">
                      <table id="example" className="table table-striped table-hover table-responsive table-sm">
                          <thead>
                              <tr>
                                  <th>Sr. No.</th>
                                  <th>Operator Name</th>
                                  <th>Division Name</th>
                                  <th>Designation</th>
                                  <th>Active Status</th>
                                  <th>Action</th>
                              </tr>
                          </thead>
                          <tbody>
                              {this.state.data.map((result) => {
                                  return (
                                      <tr>
                                          <td>{result.id}</td>
                                          <td>Karan</td>
                                          <td>Panchkula</td>
                                          <td>Operator</td>
                                          <td>Y</td>
                                          <td>View Edit</td>
                                      </tr>
                                    )
                              }) }
                          </tbody>
                      </table>
                  </div>
              </div>
          </>
      );
  }
  }
