import React, { useState, useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { Link, useParams } from 'react-router-dom';
import swal from 'sweetalert';
import axios from 'axios'

export default function Updatetreefallingrecord() {
  const [speciesData, setSpeciesData] = useState([]);
  let params = useParams();
  const  url="../getTreeFeelingRecords/" + params.id;
  //  const finalUrl = url + params.id;

  // const myImageStyle = { width: '500px', height: '600px' };
  useEffect(() => {
    getAllData();
  }, []);

  //get all data to show on the page
  const getAllData = () => {
    axios.get(`${url}`).then((res) => {
      const abc =  res.data;
      setSpeciesData(abc);
      console.log(speciesData);
      
    })
  }
   //to show the dynamic pdf
  //  const fetchPdfUrl = async () => {
  //   try {
  //     const response = await axios.get('../getTreeSpecies', params.id);
  //     console.log(response.data.pdfUrl);
  //     setPdfUrl(response.data.pdfUrl);
  //   } catch (error) {
  //     console.error('Error fetching PDF URL:', error);
  //   }
  // };


  const handleDownload = () => {
    // Make a request to the CodeIgniter backend to generate the dynamic PDF
    fetch('../getTreeSpecies', {
      method: 'GET',
      id : params.id,
      responseType: 'blob' // Set the response type to blob
    })
    .then((response) => response.blob())
    .then((blob) => {
