import React from 'react'
import '../Login.css';
import { Link } from 'react-router-dom';
import { useForm } from "react-hook-form";

export default function SelfLogin() {

    const {
        register,
        handleSubmit,
        formState: { errors },
        form,
        trigger,
      } = useForm();
    
      // useEffect(() => {
      //   if (form.isValidForm) {
      //     // console.log(fields, errors, form);
      //     // Perform api call here
      //     alert('f');
      //   }
      // }, [])
    const onSubmit = async (event) => {
        const isValid = await form.validate(event);
        alert(form.validate(event));
        if (isValid) {
          // console.log(fields, errors);
          // Perform api call here
        }
    }
//     const onSubmit = (data) => {
//         console.log(data);
//         if (data.isValidForm) {
//             console.log(data);
            

//         }
//   };

  return (
    <div>
       <div className="Auth-form-container">
            <form className="Auth-form myForm" method="post" noValidate autoComplete="off"  onSubmit={onSubmit}>
                <div className="Auth-form-content">
                <h3 className="Auth-form-title">Two Factor Authentication</h3>
                <div className="form-group mt-3">
                    <label>Mobile Number <span className='span'>*</span></label>
                    <input
                        type="number"
                        className={`form-control mt-1 ${errors.phone && "invalid"}`}
                        {...register("phone", { required: "Phone is Required",
                        pattern: {
                          value: /^\s*(?:\+?(\d{1,3}))?[-. (]*(\d{3})[-. )]*(\d{3})[-. ]*(\d{4})(?: *x(\d+))?\s*$/,
                          message: "Invalid phone no",
                        },
                       })}
                       onKeyUp={() => {
                        trigger("phone");
                      }}
                        placeholder="Enter Mobile Number"
                    />
                    <label className="error">
                    {errors.phone && (<small className="text-danger">{errors.phone.message}</small> )}
                    </label>
                </div>
                <div className="form-group mt-3">
                    <label>Year</label>
                    <input
                    type="number"
                    className="form-control mt-1"
                    placeholder="Enter Your Birth Year"
                    name="year"
                    />
                    <label className="error">
                    {errors.year ? errors.year : ""}
                    </label>
                </div>  
                <div className="d-grid gap-2 mt-3">
                    <button type="submit" className="btn btn-primary">
                    Submit
                    </button>
                </div>
                </div>
            </form>
        </div>
    </div>
  )
}
