    //     event.stopPropagation();
    //   }
    //   setValidated(true);
    // }

  return (
    <div>
      <div className="dashboard-ecommerce">
    <div className="container-fluid dashboard-content me-xxl-0 custom-container">
        <div className="row">
            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                <div className="page-header">
                    <h2 className="pageheader-title">Tree Felling</h2>
                    <div className="page-breadcrumb">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb">
                                <li className="breadcrumb-item"><a href="<?php echo base_url(); ?>dashboard" className="breadcrumb-link">Forest Departmen</a></li>
                                <li className="breadcrumb-item active" aria-current="page">Tree Felling</li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>
        </div>
        <div className="card">
            <div className="card-body">
                <div className="form-popup" style={{marginLeft: "20px"}}>
                    <div align="center">
                        <div id="success"></div>
                        <div id="error"></div>

                    </div>
                    <form id="about_form"  method="post" >
                        <div className="row">
                            <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-6">
                                <div className="form-group">
                                    <label>Select Catregory: <span className="red-text">*</span></label>
                                    <select name="cat_id" id="cat_id" className="form-control" required="required">
                                        <option value="">Select Catregory</option>
                                        <option value="Individual">Individual</option>
                                        <option value="Organization">Organization</option>
                                    </select>
                                </div>
                            </div>

                            <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6 col-6">
                                <div className="form-group">
                                    <label>Select Place : <span className="red-text">*</span></label>
                                    <select name="language_id" id="language_id" className="form-control" required="required">
                                        <option value="">Select Place</option>
                                        <option value="Balod">Balod</option>
                                        <option value="Baloda">Baloda</option>
                                        <option value="Bazar">Bazar</option>
                                        <option value="Balrampur">Balrampur</option>
                                        <option value="Bastar">Bastar</option>
                                        <option value="Bemetara">Bemetara</option>
                                        <option value="Bijapur">Bijapur</option>
                                        <option value="Bilaspur">Bilaspur</option>
                                        <option value="Dantewada">Dantewada</option>
                                        <option value="Dhamtari">Dhamtari</option>
                                        <option value="Durg">Durg</option>
                                        <option value="Gariaband">Gariaband</option>
                                        <option value="Janjgir">Janjgir</option>
                                        <option value="Champa">Champa</option>
                                        <option value="Jashpur">Jashpur</option>
                                        <option value="Kabirdham">Kabirdham</option>
                                        <option value="Kanker">Kanker</option>
                                        <option value="Kondagaon">Kondagaon</option>
                                        <option value="Korba">Korba</option>
                                        <option value="Koriya">Koriya</option>
                                        <option value="Mahasamund">Mahasamund</option>
                                        <option value="Mungeli">Mungeli</option>
                                        <option value="Narayanpur">Narayanpur</option>
                                        <option value="Raigarh">Raigarh</option>
                                        <option value="Raipur">Raipur</option>
                                        <option value="Sukma">Sukma</option>
                                        <option value="Surajpur">Surajpur</option>
                                        <option value="Surguja">Surguja</option>
                                    </select>
                                </div>
                            </div>
                            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                                <label>Enter About: <span className="red-text">*</span></label>
                                <div className="form-group">
                                    <div id="summernote">
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-2 col-lg-2 col-md-2 col-sm-2 col-2">
                                <button type="submit" id="SaveRecord" className="btn btn-primary btn-lg btn-block edit_faculty_btn" data-id="">Update</button>                    
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
     </div>
</div>
    </div>
  )
}
