import React from 'react'
import '../Login.css';
import { Link } from 'react-router-dom';
import { useFormInputValidation } from "react-form-input-validation";

export default function SelfLogin() {

    const [fields, errors, form] = useFormInputValidation({
        email_address: "",
        password: "",
    }, {
        email_address: "required|email",
        password: "required"
    });
    
      // useEffect(() => {
      //   if (form.isValidForm) {
      //     // console.log(fields, errors, form);
      //     // Perform api call here
      //     alert('f');
      //   }
      // }, [])
    
       // Check if email is valid
    // if (this.state.isvalid) {
    //     console.log(this.state);
    //   }


    const onSubmit = async (event) => {
        const isPhoneValid = this.phoneValidation();
        this.setState(
          {
            isvalid: isPhoneValid,
            message: isPhoneValid
              ? "Phone Number is Valid!"
              : "Phone Number not valid!",
          },
          () => this.props.onPhoneSubmit(this.state)
        );
        const isValid = await form.validate(event);
        if (isValid) {
          // console.log(fields, errors);
          // Perform api call here
        }
    }

  return (
    <div>
       <div className="Auth-form-container">
            <form className="Auth-form myForm" method="post" noValidate autoComplete="off"  onSubmit={onSubmit}>
                <div className="Auth-form-content">
                <h3 className="Auth-form-title">Two Factor Authentication</h3>
                <div className="form-group mt-3">
                    <label>Mobile No: <span className='span'>*</span></label>
                    <input
                        type="phone"
                        className="form-control mt-1"
                        placeholder="Enter email"
                        name="mobile_number"
                        value={this.state.mobile_number}
                        onChange={this.onChange}
                    />
                    <label className="error">
                    {errors.email_address  ? errors.email_address : ""}
                    </label>
                </div>
                <div className="form-group mt-3">
                    <label>Password</label>
                    <input
                    type="password"
                    className="form-control mt-1"
                    placeholder="Enter password"
                    name="password"
                    onBlur={form.handleBlurEvent}
                    onChange={form.handleChangeEvent}
                    value={fields.password}
                    />
                    <label className="error">
                    {errors.password ? errors.password : ""}
                    </label>
                </div>  
                <div className="d-grid gap-2 mt-3">
                    <button type="submit" className="btn btn-primary">
                    Submit
                    </button>
                </div>
                <p className="forgot-password text-right mt-2">
                <Link to='../ForgetPassword'>Forgot password?</Link>
                </p>
                </div>
            </form>
        </div>
    </div>
  )
}
