import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import { useFormInputValidation } from "react-form-input-validation";
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';



export default function Updateuser() {
    const navigate = useNavigate();
    const [role, setRole] = useState('');
    const [emailaddress, setEmailAddress] = useState('');
    const [errorMessages, setErrorMessages] = useState({});
    const [fields, errors, form] = useFormInputValidation({ email_address: "", password: ""}, {email_address: "required|email", password: "required"});
    const [authenticated, setauthenticated] = useState(localStorage.getItem(localStorage.getItem("authenticated") || false));
  
  
    // useEffect(() => {
    //   if (form.isValidForm) {
    //     // console.log(fields, errors, form);
    //     // Perform api call here
    //     alert('f');
    //   }
    // }, [])
  
    // User Login info
    const database = [
      {
        email_address: "rohit@gmail.com",
        password: "pass",
        role: "user"
      },
      {
        email_address: "rohit1@gmail.com",
        password: "pass",
        role: "rfo"
      },
      {
        email_address: "rohit2@gmail.com",
        password: "pass",
        role: "dfo"
      }
    ];
  
  
    const onSubmit = async (event) => {
      //Prevent page reload
      event.preventDefault();
      var { email_address, password } = document.forms[0];
      const isValid = await form.validate(event);
      // Find user login info
      const userData = database.find((user) => user.email_address === email_address.value);
      
      if (isValid) {
  
        // if(fields.email_address==="rohit@gmail.com"){
        //   sessionStorage.setItem("authenticated", true);
        //   navigate("/admin/adminHome");
        // }
  
        // Compare user info
        if (userData) {
          if (userData.password !== password.value) {
            // Invalid password
            setErrorMessages({ name: "password", message: errors.password });
          } else {
            // userData.role == "user" ? navigate("/") : userData.role == "rfo" ? navigate("/mainrecords") : navigate("DeptLogin");
            if(userData.role === "user"){
              navigate("/");
              window.location.reload(true);
            }else if(userData.role === "rfo"){
              navigate("/mainrecord");
              window.location.reload(true);
            }
  
  
            localStorage.setItem('token-info', JSON.stringify(userData));
            localStorage.setItem("authenticated", true)
            setauthenticated(true);
            setRole(userData.role);
            setEmailAddress(userData.emailAddress);
  
          }
        } else {
          // email_address not found
          alert({ name: "email_address", message: errors.email_address });
        }
  
        // console.log(fields, errors);
        // Perform api call here
      }
    }
  return (
    <div>
        <div className="row">
            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
                <div className="page-header my-lg-4" style={{marginLeft: "268px"}}>
                    <h2 className="pageheader-title">Tree Felling</h2>
                    <div className="page-breadcrumb">
                    <nav aria-label="breadcrumb">
                        <ol className="breadcrumb">
                            <li className="breadcrumb-item"><Link to="./mainrecord" className="breadcrumb-link">Dashboard</Link></li>
                            <li className="breadcrumb-item"><Link to="./mainrecord" className="breadcrumb-link">Manage Operator</Link></li>
                            <li className="breadcrumb-item active" aria-current="page">Update </li>
                        </ol>
                    </nav>
                    </div>
                </div>
            </div>
        </div>
      
            <div className="Auth-form-container"  style={{marginLeft: "280px", width: "70%" }}>
                <form className="Auth-form myForm" method="post" noValidate autoComplete="off"  onSubmit={onSubmit}>
                <div className="Auth-form-content">
                <Container>
                    <Row>
                        <h3 className="Auth-form-title">Update User</h3>
                        <Col xs={6}>
                            <div className="form-group mt-3">
                                <label>Operator Name <span className='span'>*</span></label>
                                <input
                                type="email"
                                className="form-control mt-1"
                                placeholder="Enter email"
                                name="email_address"
                                onBlur={form.handleBlurEvent}
                                onChange={form.handleChangeEvent}
                                value={fields.email_address}
                                />
                                <label className="error">
                                {errors.email_address  ? errors.email_address : ""} 
                                </label>
                            </div>
                        </Col>
                        <Col xs={6}>
                            <div className="form-group mt-3">
                                <label>User Role</label>
                                <input
                                type="password"
                                className="form-control mt-1"
                                placeholder="Enter password"
                                name="password"
                                onBlur={form.handleBlurEvent}
                                onChange={form.handleChangeEvent}
                                value={fields.password}
                                />
                                <label className="error">
                                {errors.password ? errors.password : ""}
                                </label>
                            </div>  
                        </Col>
                        </Row>
                        <Row>
                        <Col xs={6}>
                            <div className="form-group">
                              <label>Select Place : <span className="red-text">*</span></label>
                              <select name="place_id" id="place_id" className="form-control" required="required">
                                <option value="">Select Place</option>
                                <option value="Balod">Balod</option>
                                <option value="Baloda">Baloda</option>
                                <option value="Bazar">Bazar</option>
                                <option value="Balrampur">Balrampur</option>
                                <option value="Bastar">Bastar</option>
                                <option value="Bemetara">Bemetara</option>
                                <option value="Bijapur">Bijapur</option>
                                <option value="Bilaspur">Bilaspur</option>
                                <option value="Dantewada">Dantewada</option>
                                <option value="Dhamtari">Dhamtari</option>
                                <option value="Durg">Durg</option>
                                <option value="Gariaband">Gariaband</option>
                                <option value="Janjgir">Janjgir</option>
                                <option value="Champa">Champa</option>
                                <option value="Jashpur">Jashpur</option>
                                <option value="Kabirdham">Kabirdham</option>
                                <option value="Kanker">Kanker</option>
                                <option value="Kondagaon">Kondagaon</option>
                                <option value="Korba">Korba</option>
                                <option value="Koriya">Koriya</option>
                                <option value="Mahasamund">Mahasamund</option>
                                <option value="Mungeli">Mungeli</option>
                                <option value="Narayanpur">Narayanpur</option>
                                <option value="Raigarh">Raigarh</option>
                                <option value="Raipur">Raipur</option>
                                <option value="Sukma">Sukma</option>
                                <option value="Surajpur">Surajpur</option>
                                <option value="Surguja">Surguja</option>
                              </select>
                                <label className="error">
                                {errors.password ? errors.password : ""}
                                </label>
                            </div>  
                        </Col>
                        <Col xs={6}>
                            <div className="form-group mt-3">
                                <label>Is Active</label>
                                <input
                                type="password"
                                className="form-control"
                                placeholder="Enter password"
                                name="password"
                                onBlur={form.handleBlurEvent}
                                onChange={form.handleChangeEvent}
                                value={fields.password}
                                />
                                <label className="error">
                                {errors.password ? errors.password : ""}
                                </label>
                            </div>  
                        </Col>
                        <div className="d-grid gap-2 mt-3">
                            <button type="submit" className="btn btn-primary">
                                Submit
                            </button>
                        </div>
                    </Row>
                </Container>
                
                </div>
                </form>
            </div>
    </div>
  )
}
